                        </thead>
                        <tbody>
                            <tr>
                                <td>Plargo</td>
                                <td>{this.props.d}</td>
                            </tr>

                        </tbody>
                    </table>
                </td>
            </tbody>
            </table>
            </div>
        );
    }
}

class Box extends Component {

    constructor(props) {
        super(props)
        this.state = {
            toggle: false,
            customers: []
        }
        this.setToggle = this.setToggle.bind(this);
      }

      setToggle(){
        this.setState({
            toggle: !this.state.toggle
        })
      }
      componentDidMount() {
        this.callApi()
        .then(res => this.setState({customers: res}))
        .catch(err => console.log(err));
    }
    callApi = async () => {
        const response = await fetch('/api/'+this.props.battle_id);
        const body = await response.json();
        return body;
    }

    render() {
        var winner= "";
        //var winner_splt = this.state.customers.winner.toString().split(':')
        if (winner_splt[0]=""){
            winner = winner_splt[1]
        }else{
            winner = winner_splt[0]
        }
