        return (
            <React.Fragment>
                <div className="list_box">
                <div className="content">
                    <div className="battle_info">
                        2020-03-02
                        <br />
                        {this.state.customers.battleId}
                        <hr />
                        win
                    </div>
                <div className="divider"></div>
                <div className="winner_info">
                    <b>{this.state.customers.winner}</b>
                    <br />
                    <span className="icon-running-solid"></span> : 100
                    <br />
                    <span className="icon-skull"></span> : 50
                    <br />
                    <span className="icon-fight"></span> : 130

                </div>
                <div className="vs">
                    vs
                </div>
                <div className="loser_info">
                    <span>
                        <div className="group_name"><b>GOSTOP</b></div>
                        <span className="icon-running-solid"></span> : 150
                        <br />
                        <span className="icon-skull"></span> : 130
                        <br />
                        <span className="icon-fight"></span> : 50
                    </span>
                </div>
            </div>
            {this.state.toggle ? <Detail id={this.props.battle_id}/> : ''}

            <a className="expand" href="#" onClick={this.setToggle}>EXPAND</a>
        </div>
            </React.Fragment>
        );
    }
}

export default Box;
